mapper.insert:
INSERT INTO board (CODE, TITLE, CONTENT, REG_ID, CREATED_AT, MOD_ID, UPDATED_AT)
VALUES (
        #{code}
        , #{title}
        , #{content}
        , #{regId}
        , SYSDATE
        , #{modId}
        , SYSDATE
       )

mapper.update:
UPDATE board SET
    title = #{title}
    , content = #{content}
    , mod_id = #{modId}
    , updated_at = SYSDATE
WHERE id = #{id}
